import React, { useState } from 'react';
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  ScrollView,
  TouchableOpacity,
  Alert,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { router } from 'expo-router';
import { saveApplication } from '../utils/storage';
import { LoanApplication } from '../types';
import { loanProducts } from '../utils/loanProducts';

export default function ApplyScreen() {
  const [selectedProduct, setSelectedProduct] = useState(loanProducts[0]);
  const [amount, setAmount] = useState('');
  const [term, setTerm] = useState('');
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [monthlyIncome, setMonthlyIncome] = useState('');
  const [employmentStatus, setEmploymentStatus] = useState('');
  const [documents, setDocuments] = useState<string[]>([]);

  const pickDocument = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();

    if (status !== 'granted') {
      Alert.alert('Permission required', 'Please grant camera roll permissions');
      return;
    }

    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: false,
      quality: 0.8,
    });

    if (!result.canceled && result.assets[0]) {
      setDocuments([...documents, result.assets[0].uri]);
      Alert.alert('Success', 'Document uploaded successfully');
    }
  };

  const handleSubmit = async () => {
    if (!fullName || !email || !phone || !amount || !term || !monthlyIncome || !employmentStatus) {
      Alert.alert('Error', 'Please fill in all required fields');
      return;
    }

    const application: LoanApplication = {
      id: Date.now().toString(),
      productId: selectedProduct.id,
      productName: selectedProduct.name,
      amount: parseFloat(amount),
      term: parseInt(term),
      status: 'pending',
      applicantName: fullName,
      email,
      phone,
      monthlyIncome: parseFloat(monthlyIncome),
      employmentStatus,
      dateApplied: new Date().toISOString(),
      documents,
    };

    try {
      await saveApplication(application);
      Alert.alert(
        'Application Submitted!',
        'Your loan application has been submitted successfully. You can track its status in the Status tab.',
        [
          {
            text: 'View Status',
            onPress: () => router.push('/status'),
          },
        ]
      );

      setAmount('');
      setTerm('');
      setFullName('');
      setEmail('');
      setPhone('');
      setMonthlyIncome('');
      setEmploymentStatus('');
      setDocuments([]);
    } catch (error) {
      Alert.alert('Error', 'Failed to submit application. Please try again.');
    }
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Loan Application</Text>
        <Text style={styles.headerSubtitle}>
          Complete the form below to apply
        </Text>
      </View>

      <View style={styles.formSection}>
        <Text style={styles.sectionTitle}>Loan Details</Text>

        <Text style={styles.label}>Select Loan Type</Text>
        <View style={styles.productSelector}>
          {loanProducts.map((product) => (
            <TouchableOpacity
              key={product.id}
              style={[
                styles.productButton,
                selectedProduct.id === product.id && styles.productButtonActive,
              ]}
              onPress={() => setSelectedProduct(product)}
            >
              <Text style={styles.productIcon}>{product.icon}</Text>
              <Text
                style={[
                  styles.productButtonText,
                  selectedProduct.id === product.id && styles.productButtonTextActive,
                ]}
              >
                {product.name}
              </Text>
            </TouchableOpacity>
          ))}
        </View>

        <Text style={styles.label}>Loan Amount *</Text>
        <TextInput
          style={styles.input}
          value={amount}
          onChangeText={setAmount}
          placeholder={`$${selectedProduct.minAmount} - $${selectedProduct.maxAmount}`}
          keyboardType="numeric"
        />

        <Text style={styles.label}>Loan Term (Months) *</Text>
        <TextInput
          style={styles.input}
          value={term}
          onChangeText={setTerm}
          placeholder={`${selectedProduct.minTerm} - ${selectedProduct.maxTerm} months`}
          keyboardType="numeric"
        />
      </View>

      <View style={styles.formSection}>
        <Text style={styles.sectionTitle}>Personal Information</Text>

        <Text style={styles.label}>Full Name *</Text>
        <TextInput
          style={styles.input}
          value={fullName}
          onChangeText={setFullName}
          placeholder="John Doe"
        />

        <Text style={styles.label}>Email Address *</Text>
        <TextInput
          style={styles.input}
          value={email}
          onChangeText={setEmail}
          placeholder="john@example.com"
          keyboardType="email-address"
          autoCapitalize="none"
        />

        <Text style={styles.label}>Phone Number *</Text>
        <TextInput
          style={styles.input}
          value={phone}
          onChangeText={setPhone}
          placeholder="+1 234 567 8900"
          keyboardType="phone-pad"
        />
      </View>

      <View style={styles.formSection}>
        <Text style={styles.sectionTitle}>Financial Information</Text>

        <Text style={styles.label}>Monthly Income *</Text>
        <TextInput
          style={styles.input}
          value={monthlyIncome}
          onChangeText={setMonthlyIncome}
          placeholder="$5000"
          keyboardType="numeric"
        />

        <Text style={styles.label}>Employment Status *</Text>
        <TextInput
          style={styles.input}
          value={employmentStatus}
          onChangeText={setEmploymentStatus}
          placeholder="Full-time, Part-time, Self-employed, etc."
        />
      </View>

      <View style={styles.formSection}>
        <Text style={styles.sectionTitle}>Documents</Text>
        <Text style={styles.label}>Upload Supporting Documents</Text>
        <Text style={styles.helperText}>
          Upload ID, proof of income, or other relevant documents
        </Text>

        <TouchableOpacity style={styles.uploadButton} onPress={pickDocument}>
          <Text style={styles.uploadButtonText}>üìé Upload Document</Text>
        </TouchableOpacity>

        {documents.length > 0 && (
          <View style={styles.documentsContainer}>
            <Text style={styles.documentsTitle}>
              Uploaded: {documents.length} document(s)
            </Text>
          </View>
        )}
      </View>

      <TouchableOpacity style={styles.submitButton} onPress={handleSubmit}>
        <Text style={styles.submitButtonText}>Submit Application</Text>
      </TouchableOpacity>

      <View style={styles.disclaimer}>
        <Text style={styles.disclaimerText}>
          By submitting this application, you agree to our terms and conditions
          and authorize us to verify the information provided.
        </Text>
      </View>
    </ScrollView>
  );
}

